import React, { Component } from 'react';
import { View, ScrollView, TouchableOpacity } from 'react-native';
import Content from './Content';
import { getApi } from './Api';

export default class FeaturedNews extends Component {
  constructor(props) {
    super(props);
    this.state = {
      NewsData: []
    }
    this.api = getApi();
  }
  componentDidMount() {
    if (!this.api) return;
    this.api.get(`/wp-json/wp/v2/posts?better_featured_image`)
      .then(res => this.setState({
        NewsData: res.data,
      }))
  }
  render() {
    const { NewsData, } = this.state;
    return (
      <ScrollView>
      <View>
        
        {/* <TouchableOpacity onPress={ () => this.props.navigation.navigate('NewsPost')} >
          <Text>
            Pukhraj Dhamu
          </Text>
          </TouchableOpacity> */}
        
        {NewsData.map(NewsData => (
          <Content key={NewsData.id} NewsData={NewsData} />
        ))}
      </View>
      </ScrollView>
    );
  }
}
