import React, { Component } from 'react';
import { View, Text, SafeAreaView, StyleSheet, Image } from 'react-native';
import { getApi } from './Api';

export default class Content extends Component {

  constructor(props) {
    super(props);
    this.state = {
      Newsimg: []
    }
    this.api = getApi();
  }
  componentDidMount() {
    if (!this.api) return;
    this.api.get(`/wp-json/wp/v2/posts?better_featured_image`)
      .then(res => this.setState({
        Newsimg: res.data,
      }))
  }

  render() {
    const { title, excerpt, medium, post, source_url } = this.props.NewsData;
    return (
      <SafeAreaView style={styles.scrollView}>

        <View style={styles.item}>
          <Image
            style={styles.stretch}
            source={{ uri: source_url }}
          />

          <Text style={styles.news} >
            {title.rendered}
          </Text>
          <Text>{excerpt.rendered}</Text>
        </View>

      </SafeAreaView>


    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,

  },
  item: {
    padding: 1,
    marginVertical: 8,
    marginHorizontal: 16,
  },
  title: {
    fontSize: 32,
  },
  scrollView: {

    marginHorizontal: 0,
  },
  news: {
    marginTop: 30,
  },
  logo: {
    width: 66,
    height: 58,
  },
  stretch: {
    width: 300,
    height: 200,
    
},
});
