import React, { Component } from 'react';
import { View, ScrollView, TouchableOpacity } from 'react-native';
import Content from './Content';
import { getApi } from './Api';

export default class Post extends Component {
  constructor(props) {
    super(props);
    this.state = {
      NewsData: []
    }
    this.api = getApi();
  }
  componentDidMount() {
    if (!this.api) return;
    this.api.get(`/wp-json/wp/v2/posts?better_featured_image`)
      .then(res => this.setState({
        NewsData: res.data,
      }))
  }
  render() {
    const { NewsData, } = this.state;
    return (
        
      <ScrollView>
          
          <View style={styles.item}>
            <TouchableOpacity onPress={ () => this.props.navigation.navigate('NewsPost')} >
              <Image
                style={styles.stretch}
                source={{ uri: better_featured_image.media_details.sizes.medium.source_url }}
              />
               </TouchableOpacity>
              <Text style={styles.news} >
                {title.rendered}
              </Text>
              <Text>{excerpt.rendered}</Text>
            </View>
      </ScrollView>
    );
  }
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
    },
    item: {
      padding: 1,
      marginVertical: 8,
      marginHorizontal: 16,
    },
    title: {
      fontSize: 32,
    },
    scrollView: {
  
      marginHorizontal: 0,
    },
    news: {
      marginTop: 30,
    },
    logo: {
      width: 66,
      height: 58,
    },
    stretch: {
      width: 300,
      height: 200,
    },
  });
