import axios from 'axios';
let api = null;

export function initializeAxios() {
    api = axios.create({
        baseURL: 'http://192.168.225.75/wordpress',
        timeout: 1000000,
      });
}
export function getApi() {
    if(!api) {
        initializeAxios();
    }
    return api;
}
